import React from 'react'
import styled from 'styled-components';

interface ListProps {
  list: Array<string>;
  onDelete: (index: number) => void;
  hideOdd: boolean;
}

const ListStyle = styled.div`
  & > div:not(:last-child) {
    border-bottom: 0;
  }

  & > div:last-child {
    border-bottom: 1px solid;
  }

  & > div button {
    border-radius: 10px;
  }
`;
const ListItem = styled.div`
  display: flex;
  padding: 8px;
  justify-content: space-between;
  border: 1px solid #000;
`;

const renderListItem = (item: string, index: number, hideOdd: boolean) => {
  const isOdd = (index + 1) % 2 > 0;
  if (!hideOdd && !isOdd) {
    <ListItem key={`list-item-${index}`}>
      {index + 1}. {item}
      <button onClick={() => { onDelete(index) }}> x </button>
    </ListItem>
  }
}

const isOdd = (index: number) => {
  return (index + 1) % 2 > 0;
}

export default function List(props: ListProps) {
  const { list, onDelete, hideOdd } = props;
  return (
    <ListStyle>
      {list.map((item, index) => {
        if (!hideOdd && !isOdd(index)) {
          return (
            <ListItem key={`list-item-${index}`}>
              {index + 1}. {item}
              <button onClick={() => { onDelete(index) }}> x </button>
            </ListItem>
          )
        }
      })};
    </ListStyle>
  )
}
