import React from 'react';
import styled from 'styled-components';

interface InputFieldProps {
  onAdd: (value: string) => void;
}

const InputFieldStyle = styled.div`
  @media (max-width: 768px) {
    align-self: flex-end;
  }
`;

const ErrorMessage = styled.div`
  color: #ff0000;
`;

const AddElement = styled.div`
  display: flex;
  & button {
    flex-grow: 1;
    margin-left: 8px;
  }
`

export default function InputField(props: InputFieldProps) {
  const { onAdd } = props;

  const [value, setValue] = React.useState<string | ''>('');
  const [errorMsg, setErrorMsg] = React.useState<Object>(false);

  const handleOnChange = (e: React.FormEvent<HTMLInputElement>) => {
    const { value } = e.target as HTMLInputElement;
    setValue(value);
  }

  const addItem = () => {
    setErrorMsg({});
    validateName(value);
  }

  const validateName = (name: string) => {
    if (!value || value.replace(/\s/g, '') === "") {
      setErrorMsg({ message: "Name is required" });
    } else if (value && value.length < 5) {
      setErrorMsg({ message: "Name should have at least 5 characters" });
    }
  }

  const renderError = () => {
    if (errorMsg) {
      return (
        <ErrorMessage>{errorMsg}</ErrorMessage>
      )
    }
  }

  return (
    <InputFieldStyle>
      {renderError()}
      <AddElement>
        <input type="text" value={value} onChange={(e) => {
          handleOnChange(e);
        }} />
        <button onClick={addItem}>Add</button>
      </AddElement>
    </InputFieldStyle>
  )
}
