import React from 'react';
import styled from 'styled-components';
import Container from "./Container";
import InputField from './InputField';
import List from './List';

const ToolBar = styled.div`
  display: flex;
  justify-content: space-evenly;
  flex-direction: column;

  @media (min-width: 768px) {
    flex-direction: row;
  }

  & > div {
    display: flex;
    padding: 8px;
    flex-direction: column;  
  }
`;

const SearchInput = styled.div`
  @media (min-width: 768px) {
    align-self: flex-end;
  }
`;


export default function Tab1() {
  const [list, setList] = React.useState<Array<string>>([]);
  const [hideOdd, setHideOdd] = React.useState<boolean>(false);

  const handleAdd = (value: string) => {
    const newList = list.map(item => item);
    newList.push(value);
    setList(newList);
  }

  const handleDelete = (index: number) => {
    const toDelete = list[index];
    const newList = list.filter((item) => { return item !== toDelete })
    setList(newList);
  }

  const handleOnChange = (e: React.FormEvent<HTMLInputElement>) => {
    const { value } = e.target as HTMLInputElement;
    const newValue = value as unknown as boolean;
    setHideOdd(newValue);
  }

  const checkNames = (name: string) => {
    return list.includes(name);
  }

  return (
    <Container>
      <ToolBar>
        <InputField onAdd={handleAdd} checkNames={checkNames} />
        <SearchInput>
          <input type="text" placeholder="Search" />
        </SearchInput>
        <div>
          Hide odd toggle {hideOdd}
          <input type="checkbox" checked={hideOdd} onChange={(e) => handleOnChange(e)} />
        </div>
      </ToolBar>
      <List list={list} onDelete={handleDelete} />
    </Container>
  )
}
