import React from 'react';
import styled from 'styled-components';
import Container from "./Container";

const ToolBar = styled.div`
  display: flex;
  & > div: {
    display: flex;
    flex-direction: column;  
  }
`;

const SearchInput = styled.div`
  display: flex;
  flex-direction: column;
`;

const AddToggle = styled.div`
  display: flex;
  flex-direction: column;
`;

export default function Tab1() {
  return (
    <Container>
      {/* Toolbar */}
      <ToolBar>
        <SearchInput>
          {/* error */}
          <div>
            <input type="text" />
            <button>Add</button>
          </div>
        </SearchInput>
        {/* search box */}
        <input type="text" />
        {/* toggle */}
        <AddToggle>
          Hide add toggle
          <input type="checkbox" />
        </AddToggle>
      </ToolBar>
    </Container>
  )
}
